styles.css:
/*Tools*/
.relative {
    position: relative;
}

.stretched-link-btn a::after,
.stretched-link::after {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: 1;
    content: "";
}

.shadow-sm {
    -webkit-box-shadow: 0.5px 1px 1px hsl(32deg 100% 85% / 0.7);
    box-shadow: 0.5px 1px 1px hsl(32deg 100% 85% / 0.7);
    -webkit-transition: border ease-in-out 0.2s,
        -webkit-box-shadow ease-in-out 0.2s;
    transition: border ease-in-out 0.2s, -webkit-box-shadow ease-in-out 0.2s;
    -o-transition: box-shadow ease-in-out 0.2s, border ease-in-out 0.2s;
    transition: box-shadow ease-in-out 0.2s, border ease-in-out 0.2s;
    transition: box-shadow ease-in-out 0.2s, border ease-in-out 0.2s,
        -webkit-box-shadow ease-in-out 0.2s;
}

.shadow-md {
    -webkit-box-shadow: 1px 2px 2px hsl(32deg 100% 85%),
        2px 4px 4px hsl(32deg 100% 85%), 3px 6px 6px hsl(32deg 100% 85%);
    box-shadow: 1px 2px 2px hsl(32deg 100% 85%), 2px 4px 4px hsl(32deg 100% 85%),
        3px 6px 6px hsl(32deg 100% 85%);
}

.shadow-lg {
    -webkit-box-shadow: 1px 2px 2px hsl(32deg 100% 85% / 0.2),
        2px 4px 4px hsl(32deg 100% 85% / 0.2), 4px 8px 8px hsl(32deg 100% 85% / 0.2),
        8px 16px 16px hsl(32deg 100% 85% / 0.2),
        16px 32px 32px hsl(32deg 100% 85% / 0.2);
    box-shadow: 1px 2px 2px hsl(32deg 100% 85% / 0.2),
        2px 4px 4px hsl(32deg 100% 85% / 0.2), 4px 8px 8px hsl(32deg 100% 85% / 0.2),
        8px 16px 16px hsl(32deg 100% 85% / 0.2),
        16px 32px 32px hsl(32deg 100% 85% / 0.2);
}

.drop-shadow-sm {
    -webkit-filter: drop-shadow(0.5px 1px 1px hsl(32deg 100% 85%));
    filter: drop-shadow(0.5px 1px 1px hsl(32deg 100% 85%));
}

.drop-shadow-md {
    -webkit-filter: drop-shadow(1px 2px 3px hsl(32deg 100% 85% / 0.3)) drop-shadow(2px 4px 6px hsl(32deg 100% 85% / 0.3)) drop-shadow(4px 8px 12px hsl(32deg 100% 85% / 0.3));
    filter: drop-shadow(1px 2px 3px hsl(32deg 100% 85% / 0.3)) drop-shadow(2px 4px 6px hsl(32deg 100% 85% / 0.3)) drop-shadow(4px 8px 12px hsl(32deg 100% 85% / 0.3));
}

.drop-shadow-lg {
    -webkit-filter: drop-shadow(1px 2px 3px hsl(32deg 100% 85% / 0.3)) drop-shadow(2px 4px 6px hsl(32deg 100% 85% / 0.3)) drop-shadow(4px 8px 12px hsl(32deg 100% 85% / 0.3)) drop-shadow(8px 16px 16px hsl(32deg 100% 85% / 0.3)) drop-shadow(16px 32px 32px hsl(32deg 100% 85% / 0.3));
    filter: drop-shadow(1px 2px 3px hsl(32deg 100% 85% / 0.3)) drop-shadow(2px 4px 6px hsl(32deg 100% 85% / 0.3)) drop-shadow(4px 8px 12px hsl(32deg 100% 85% / 0.3)) drop-shadow(8px 16px 16px hsl(32deg 100% 85% / 0.3)) drop-shadow(16px 32px 32px hsl(32deg 100% 85% / 0.3));
}

.text-right {
    text-align: right;
}

@media (min-width: 782px) {
    .text-right-md {
        text-align: right;
    }
}

.article-index {
    -webkit-box-ordinal-group: 0;
    -ms-flex-order: -1;
    order: -1;
}

@media (min-width: 782px) {
    .article-index {
        -webkit-box-ordinal-group: unset;
        -ms-flex-order: unset;
        order: unset;
    }
}

/*Header*/
.logo-block {
    cursor: pointer;
}

.logo-block:hover {
    text-decoration: underline;
}

header .custom-logo-link::after {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: 1;
    content: "";
}

@media (min-width: 782px) {
    header .header-line {
        -webkit-box-pack: justify !important;
        -ms-flex-pack: justify !important;
        justify-content: space-between !important;
    }
}

/*Query Block*/
.wp-block-query:not(main) .wp-block-post-template .wp-block-post {
    position: relative;
    padding: 10px 20px;
    cursor: pointer;
    border: 1px solid var(--wp--preset--color--custom-accent-3);
    background-color: #fafafa;
    -webkit-box-shadow: 0.5px 1px 1px hsl(32deg 100% 85% / 0.7);
    box-shadow: 0.5px 1px 1px hsl(32deg 100% 85% / 0.7);
    -webkit-transition: border ease-in-out 0.2s,
        -webkit-box-shadow ease-in-out 0.2s;
    transition: border ease-in-out 0.2s, -webkit-box-shadow ease-in-out 0.2s;
    -o-transition: box-shadow ease-in-out 0.2s, border ease-in-out 0.2s;
    transition: box-shadow ease-in-out 0.2s, border ease-in-out 0.2s;
    transition: box-shadow ease-in-out 0.2s, border ease-in-out 0.2s,
        -webkit-box-shadow ease-in-out 0.2s;
}

.wp-block-query:not(main) .wp-block-post-template .wp-block-post:hover {
    border: 1px solid var(--wp--preset--color--custom-accent-3);
    -webkit-box-shadow: 1px 2px 2px hsl(32deg 100% 85%),
        2px 4px 4px hsl(32deg 100% 85%), 3px 6px 6px hsl(32deg 100% 85%);
    box-shadow: 1px 2px 2px hsl(32deg 100% 85%), 2px 4px 4px hsl(32deg 100% 85%),
        3px 6px 6px hsl(32deg 100% 85%);
}

.wp-block-query:not(main) .wp-block-post-template .wp-block-post .wp-block-post-excerpt__more-link::after {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: 1;
    content: "";
}

.wp-block-query:not(main) .wp-block-post-template .wp-block-post.format-link {
    position: relative;
    border-top-color: var(--wp--preset--color--custom-accent-1);
    border-top-width: 2px;
    border-right-color: var(--wp--preset--color--custom-accent-2);
    border-right-width: 2px;
    border-bottom-color: var(--wp--preset--color--custom-accent-2);
    border-bottom-width: 2px;
    border-left-color: var(--wp--preset--color--custom-accent-1);
    border-left-width: 2px;
    padding-top: 30px;
}

.wp-block-query:not(main) .wp-block-post-template .wp-block-post.format-link::before {
    content: "Article externe ↗️";
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    padding: 4px 6px;
    color: var(--wp--preset--color--custom-base-2);
    font-size: 0.8rem;
    background-color: var(--wp--preset--color--custom-accent-1);
}

/*Buttons*/
.wp-block-button__link:hover {
    background: -webkit-gradient(linear,
            left top,
            right top,
            from(rgba(73, 40, 21, 1)),
            to(rgba(168, 68, 16, 1)));
    background: -o-linear-gradient(left,
            rgba(73, 40, 21, 1) 0%,
            rgba(168, 68, 16, 1) 100%);
    background: linear-gradient(90deg,
            rgba(73, 40, 21, 1) 0%,
            rgba(168, 68, 16, 1) 100%);
}

input[type="button"],
input[type="reset"],
input[type="submit"] {
    background: -o-linear-gradient(315deg,
            rgb(168, 68, 16) 0%,
            rgb(241, 137, 45) 100%);
    background: linear-gradient(135deg,
            rgb(168, 68, 16) 0%,
            rgb(241, 137, 45) 100%);
    border-radius: 0px;
    border-top-color: #a84410;
    border-top-width: 2px;
    border-top-style: solid;
    border-right-color: #f1892d;
    border-right-width: 2px;
    border-right-style: solid;
    border-bottom-color: #f1892d;
    border-bottom-width: 2px;
    border-bottom-style: solid;
    border-left-color: #a84410;
    border-left-width: 2px;
    border-left-style: solid;
    color: #ffffff;
    font-style: normal;
    font-weight: 700;
    line-height: 1.2;
    padding-top: 5px;
    padding-right: 10px;
    padding-bottom: 5px;
    padding-left: 10px;
    text-transform: uppercase;
    border-style: solid;
    padding-top: 5px;
    padding-right: 10px;
    padding-bottom: 5px;
    padding-left: 10px;
}

button:disabled,
input[type="button"]:disabled,
input[type="reset"]:disabled,
input[type="submit"]:disabled {
    opacity: 0.7;
}

/*About Cards*/

.about-card {
    cursor: pointer;
    -webkit-transition: background-color ease-in-out 0.2s,
        -webkit-box-shadow ease-in-out 0.2s;
    transition: background-color ease-in-out 0.2s,
        -webkit-box-shadow ease-in-out 0.2s;
    -o-transition: box-shadow ease-in-out 0.2s, background-color ease-in-out 0.2s;
    transition: box-shadow ease-in-out 0.2s, background-color ease-in-out 0.2s;
    transition: box-shadow ease-in-out 0.2s, background-color ease-in-out 0.2s,
        -webkit-box-shadow ease-in-out 0.2s;
    -webkit-box-shadow: 0.5px 1px 1px hsl(32deg 100% 85% / 0.7);
    box-shadow: 0.5px 1px 1px hsl(32deg 100% 85% / 0.7);
    -webkit-transition: background ease-in-out 0.2s,
        -webkit-box-shadow ease-in-out 0.2s;
    transition: background ease-in-out 0.2s, -webkit-box-shadow ease-in-out 0.2s;
    -o-transition: box-shadow ease-in-out 0.2s, background ease-in-out 0.2s;
    transition: box-shadow ease-in-out 0.2s, background ease-in-out 0.2s;
    transition: box-shadow ease-in-out 0.2s, background ease-in-out 0.2s,
        -webkit-box-shadow ease-in-out 0.2s;
}

.about-card:hover {
    -webkit-box-shadow: 1px 2px 2px hsl(32deg 100% 85%),
        2px 4px 4px hsl(32deg 100% 85%), 3px 6px 6px hsl(32deg 100% 85%);
    box-shadow: 1px 2px 2px hsl(32deg 100% 85%), 2px 4px 4px hsl(32deg 100% 85%),
        3px 6px 6px hsl(32deg 100% 85%);
    background-color: #fafafa !important;
}

/*Breadcrumb*/
.site-breadcrumb {
    font-size: 0.8em;
}

/*Review Masonry*/
.review-masonry {
    -webkit-columns: 1;
    -moz-columns: 1;
    columns: 1;
    -webkit-column-gap: 20px;
    -moz-column-gap: 20px;
    column-gap: 20px;
}

@media (min-width: 782px) {
    .review-masonry {
        -webkit-columns: 2;
        -moz-columns: 2;
        columns: 2;
    }
}

.review-masonry .item {
    -webkit-column-break-inside: avoid;
    -moz-column-break-inside: avoid;
    break-inside: avoid-column;
}
